import React, {useState,useEffect} from 'react';
import axios from 'axios';
import {useForm} from 'react-hook-form'



function App(){

    const {register , formState:{isSubmitting}} = useForm();


    useEffect(() => {
        axios.get('http://localhost:3306/')
          .then(res => console.log(res))
          .catch()
      })
    return(
        <div ref={register}>
            <h1>Hello World!</h1>
            <form >
                <label for='id' >아이디</label>
                <input id='id' placeholder='아이디' {...register('id', {
                    required:"id는 필수로 입력되어야 합니다",
                    pattern:{
                        value: /\S+@\S+\.\S+/,
                        message: "이메일 형식에 맞지 않습니다.",
                    }
                })} />
                <br/>
                <label for='passwd'>비밀번호</label>
                <input type='password' id='passwd' placeholder='비밀번호' {...register('password', {
                    required:'비밀번호는 필수로 입력되어야 합니다',
                    minLength:{
                        value:8,
                        message:'비밀번호는 8자리 이상으로 입력되어야 합니다.'
                    }
                })} ></input>
                <br></br>
                <button type='submit' disabled={isSubmitting}>로그인</button>

            </form>
        </div>

    )
}

export default App;
