import React, {useState,useEffect} from 'react';
import axios from 'axios';
import {useForm} from 'react-hook-form'



function App(){

    const {register, handleSubmit, formState:{isSubmitting}} = useForm();


    useEffect(() => {
        axios.get('http://localhost:3306/')
          .then(res => console.log(res))
          .catch()
      })
    return(
        <div>
            <h1>Hello World!</h1>
            <form onSubmit={handleSubmit((data) => alert(JSON.stringify(data)))}>
                <label for='id' >아이디</label>
                <input id='id' placeholder='아이디' {...register('id', {
                    required:"id는 필수로 입력되어야 합니다",
                    pattern:{

                    }
                })} />
                <br/>
                <label for='passwd'>비밀번호</label>
                <input type='password' id='passwd' placeholder='비밀번호' {...register('password')} ></input>
                <br></br>
                <button type='submit' disabled={isSubmitting}>로그인</button>

            </form>
        </div>

    )
}

export default App;
