import React, {useState,useEffect} from 'react';
import axios from 'axios';
import {useForm} from 'react-hook-form'



function App(){


    const {register, handleSubmit, formState:{isSubmitting, errors}} = useForm();


    return(
        <div ref={register}>
            <h1>Hello World!</h1>
            <form  onSubmit={handleSubmit(async (data) => {
        await new Promise((r) => setTimeout(r, 1000));
        alert(JSON.stringify(data)); })}>
                <label for='email' >아이디</label>
                <input  {...register('email', {
                    required:"이메일은 필수로 입력되어야 합니다",
                    pattern:{
                        value: /\S+@\S+\.\S+/,
                        message: "이메일 형식에 맞지 않습니다.",
                    }
                })} id='email' type='email' placeholder='이메일' />
                <br/>
                <label for='password'>비밀번호</label>
                <input {...register('password', {
                    required:'비밀번호는 필수로 입력되어야 합니다',
                    minLength:{
                        value:8,
                        message:'비밀번호는 8자리 이상으로 입력되어야 합니다.'
                    }
                })} type='password' id='password' placeholder='비밀번호' />
                <br></br>
                {errors.message && errors.message.message}
                <button  disabled={isSubmitting} type='submit'>로그인</button>

            </form>
        </div>

    )
}

export default App;
