import React, {useState,useEffect} from 'react';
import axios from 'axios';
import {useForm} from 'react-hook-form'



function App(){
    const {register, handleSubmit, formState:{errors}} = useForm();

    const onSubmit = (data)=>{console.log(data);}



    return(
        <div>
            <form onSubmit={handleSubmit(onSubmit)} method='post' action='http://localhost:3306/login'>
                <label for="nickname">Nickname</label>
                <input id='nickname' name='nickname' {...register('nickname',{
                    required:'nickname required',
                    maxLength:{
                        value:10,
                        message:'wrong nickname less than 10'
                    }
                })}></input><br></br>
                {errors.nickname && <span>{errors.nickname.message}</span>} <br></br>


                <label for="email">Email</label>
                <input id='email' name='email' {...register('email', {
                    required:"email required",
                    pattern:{
                        value: /\S+@\S+\.\S+/,
                        message:'wrong form email'

                    }
                })}></input><br></br>
                {errors.email && <span>{errors.email.message}</span>}<br></br>


                <label for="password">Password</label>
                <input id='password' name='password' {...register('password', {
                    required:'password required',
                    minLength:{
                        value:8,
                        message:'wrong form password more than 8'
                    }
                })}></input><br></br>
                {errors.password && <span>{errors.password.message}</span>}<br></br>

                <button type='submit'>Login</button>

            </form>

        </div>
    )
    }

    export default App;
    /* OAuth, form을 통한 회원가입 진행*/
